import React, { useEffect } from 'react'

import useFetch from 'hooks/useFetch'

const CurrentUserCheker=({children})=>{
    const [{response}, doFetch] = useFetch('/user')

    useEffect(()=>{
        console.log('initUseeefectCheker')
        doFetch()
    },[])
    return children
}

export default CurrentUserCheker
